import React, { Component } from 'react'; //import React Component
//import './style.css';
import 'whatwg-fetch';
import './App.css';
//var rapid = new RapidAPI("default-application_5bf3605ee4b02e4415402cd8", "99f1d82f-be7b-42ee-9ec2-b4f3c7f8c138");
import $ from 'jquery'; 

export class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cityName:"",
      cityID:"",
      output:"",
      zip:"",
      cuisine:[]
    };
    this.changeZip=this.changeZip.bind(this);
    this.changeMoney=this.changeMoney.bind(this);
    this.handleClick=this.handleClick.bind(this);
  }
  changeZip(event) {
    this.setState({zip: event.target.value});
  }
  changeCity(event) {
    this.setState({cityName:event.target.value});
  }
  changeCuisine(cuisine) {
    this.setState({cuisine:cuisine});
  }
  handleClick(event) {
    this.fetchData();
  }

  componentDidMount() {
    this.fetchData();
  }

  fetchData() {
    let cityJSON = fetchCity(this.state.cityName);
    let cityID = cityJSON.location_suggestions[0].entity_id;
    let cuisineJSON = fetchCuisine(cityID);
    changeCuisine(cuisineJSON.cuisines)
  }

  fetchCity(cityName) {
    let url = 'https://developers.zomato.com/api/v2.1/locations?query=' + cityName;
    return fetch(url, {
      method: 'GET',
      headers: {
        "Content-Type": "application/json",
        "user-key": 'd0325b0e365e86fe688a11fe58eaf786'
      }
    })
    .then(function(response) {
      return response.json();
    })
    .then(function(response) {
      console.log(response);
    })
    .catch(function(error) {
      console.error(error);
    });
  }

  fetchCuisine(cityID) {
    let url = 'https://developers.zomato.com/api/v2.1/cuisines?city_id=279' + cityID;
    return fetch(url, {
      method: 'GET',
      headers: {
        "Content-Type": "application/json",
        "user-key": 'd0325b0e365e86fe688a11fe58eaf786'
      }
    })
    .then(function(response) {
      return response.json();
    })
    .then(function(response) {
      console.log(response);
    })
    .catch(function(error) {
      console.error(error);
    });
  }

  /*fetchData() {

    
   let url = 'https://developers.zomato.com/api/v2.1/location_details?entity_id=279&entity_type=city';
    return fetch(url, {
      method: 'GET',
      headers: {
        "Content-Type": "application/json",
        "user-key": 'd0325b0e365e86fe688a11fe58eaf786'
      }
    })
    .then(function(response) {
      return response.json();
    })
    .then(function(response) {
      console.log(response);
    })
    .catch(function(error) {
      console.error(error);
    });  
  }*/
  
  render() {
    //let data = this.fetchData()
    return (
      <div className="bodyContainer">
        <div className="container">
          <h1>Food for Thought</h1>
          <Intro></Intro>
          <div className="footer">
            <Submit></Submit>
          </div>
        </div>
      </div>

    )
  }
}

class Intro extends Component {

  render() {
    return(
      <div className="introContainer">
        <div className="introContent">
          <h5><strong>Food for Thoughts</strong> is an app that helps users decide what restaurant to eat at.</h5>
          <p>Input your zip code and amount of money your willing to spend below to get started!</p>
        </div>
        <div className="zipMoney">
          <div className="zipCode">
            <input type="textarea"/>
            <label>Zip Code</label>
          </div>
          <div className="money">
            <h6>$$$</h6>
            <label>Price Range</label>
          </div>
        </div>
      </div>
    );
  }
}

class Category extends Component {

  render() {
    let data = this.props.data;
    console.log(data)
    return (
      <div>
        <h5>To fetch accurate restaurants that match your cravings, we'll need you to answer a few questions.</h5>
        <p>Select your preferred category below.</p>
      </div>
    );
  }
}

class Submit extends Component {
  render() {
    return(
      <div className="arrowSubmit">
        >>>
      </div>
    );
  }
}

export default App;
